package datatypes;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/** Assignment Operators are (=, +=, -+=, *=, /= and %=)
		 * a = 7 meaning a = 7
		 * a+=7 means a = a+7
		 * a-=7 means a = a-7
		 * a*=7 means a = a*7
		 * a/=7 means a = a/7
		 * a%=7 means a = a%7
		 * Examples:
		 */
		int a = 87;
		int result = a-7;
		int result1 = a/7;
		System.out.println(a+=7);//94
		System.out.println(result);//80 NA
		System.out.println(a*=7);//609
		System.out.println(result1);//12.4
		System.out.println(a%=7);// 3

		
	}

}
